C++ CODE:

void sort012(int a[], int n){

    int num0=0,num2=0;

    for(int i=0;i < n;i++){

        if(a[i]==0) num0++;

        else if(a[i]==2) num2++;

    }

    for(int i=0;i < n  i++){

        if(i>=0 && i < num0) a[i]=0;

        else if(i>=num0 && i < (n-num2)) a[i]=1;

        else a[i]=2;

    }

}


JAVA CODE:

public static void sort012(int a[], int n){

    int zero=0, one=0, two=0;

    for(int i=0; i < a.length; i++){

        if(a[i] == 0){

            zero++;

        }

        else if(a[i] == 1){

            one++;

        }

        else{

            two++;

        }

    }

    for(int i=0; i < a.length; i++){

        if(i < zero){

            a[i] = 0;

        }

        else if(i>=zero && i < (one+zero)){

            a[i] = 1;

        }

        else{

            a[i] = 2;

        }

    }

}

