C++ CODE:
 long long maxSub_arraySum(int arr[], int n){
 int res = arr[0];
 int curr = arr[0];
 for(int i=1;i < n;i++){
 curr = max(curr+arr[i], arr[i]);
 res = max(res,curr);
 }
 return res;
 }

 JAVA CODE:
 class Solution{
 long maxSub_arraySum(int arr[], int n){
 long max=Integer.MIN_VALUE;
 long sum=0;
 for(int i=0;i < n;i++){
 sum+=arr[i];
 if(max < sum)
 max=sum;
 //dont carry negative sum
 if(sum < 0)
 sum=0;
 }
 return max;
 }
 }

C++ CODE:
 class Solution{
 public:
 int next (int prev, int n){
 if(prev+1 > n/2) return (n-prev-1)*2;
 else{
 return (prev+1)*2-1;
 }
 }
 void arrange (int start, int n, long long *arr){
 int swap_index = next(start, n);
 while(arr[swap_index] > 0){
 swap(arr[swap_index], arr[start]);
 arr[swap_index] *= -1;
 swap_index = next(swap_index, n);
 }
 }
 void rearrange(long long *arr, int n){
 for(int i=0; i < n; i++){
 if(arr[i] > 0)
 arrange(i, n, arr);
 }
 for(int i=0; i < n; i++){
 arr[i] *= -1;
 }
 }
 }

 JAVA CODE:
 public static void rearrange(long arr[], int n){
 int max = (int)arr[n-1] + 1;
 int max_i = n-1;
 int min_i = 0;
 for(int i=0; i < n; i++){
 if(i%2 == 0){
 arr[i] = (arr[max_i]%max)*max + arr[i];
 max_i--;
 }
 else{
 arr[i] = (arr[min_i]%max)*max + arr[i];
 min_i++;
 }
 }

for(int i=0; i < n; i++){
 arr[i] = arr[i]/max;
 }
 }

