C++ CODE:
 long long int count(int coins[], int n, int sum) {
 long long int dp[n+1][sum+1];
 for(int i=0;i<=sum;i++){
 dp[0][i]=0;
 }
 for(int i=0;i<=n;i++){
 dp[i][0]=1;
 }
 for(int i=1;i<=n;i++){
 for(int j=1;j<=sum;j++){
 if(coins[i-1]>j){
 dp[i][j]=dp[i-1][j];
 }
 else{
 dp[i][j]=dp[i][j-coins[i-1]]+dp[i-1][j];
 }
 }

 }
 return dp[n][sum];
 }

 JAVA CODE:
 class Solution { public long count(int coins[], int N, int sum) {
 long dp[][]=new long[N+1][sum+1];
 for(int i=N-1;i>=0;i--){
 for(int j=0;j<=sum;j++){
 if(j==0){
 dp[i][j]=1;
 continue;
 }
 long x=0,y=0;
 if(j-coins[i]>=0)
 x=dp[i][j-coins[i]];
 y=dp[i+1][j];
 dp[i][j]=x+y;
 }
 }
 return dp[0][sum];
 }
 }

