 for i in range(1,n+1):
 for j in range(1,m+1):
 if text1[i-1]==text2[j-1]:
 dp[i][j]=dp[i-1][j-1]+1
 else:
 dp[i][j]=max(dp[i-1][j],dp[i][j-1])
 return dp[n][m]

C++ CODE:\n
class Graph {\n
    // No. of vertices\n
    int V;\n

    // Pointer to an array containing adjacency lists\n
    list<int>* adj;\n

    // Used by isCyclic()\n
    bool isCyclicUtil(int v, bool visited[], bool* rs);\n

    public:\n
    Graph(int V);\n
    void addEdge(int v, int w);\n
    bool isCyclic();\n
};\n

Graph::Graph(int V){\n
    this->V = V;\n
    adj = new list<int>[V];\n
}\n

void Graph::addEdge(int v, int w){\n
    // Add w to v’s list.\n
    adj[v].push_back(w);\n
}\n

// DFS function to find if a cycle exists\n
bool Graph::isCyclicUtil(int v, bool visited[],bool* recStack){\n
    if (visited[v] == false) {\n
        // Mark the current node as visited\n
        // and part of recursion stack\n\n
        visited[v] = true;\n
        recStack[v] = true;\n

        // Recur for all the vertices adjacent to this\n
        // vertex\n
        list<int>::iterator i;\n
        for (i = adj[v].begin(); i != adj[v].end(); ++i) {\n
            if (!visited[*i] && isCyclicUtil(*i, visited, recStack))\n
                return true;\n
            else if (recStack[*i])\n
                return true;\n
        }\n
    }\n

    // Remove the vertex from recursion stack\n
    recStack[v] = false;\n
    return false;\n
}\n

// Returns true if the graph contains a cycle, else false\n
bool Graph::isCyclic(){\n
    // Mark all the vertices as not visited\n
    // and not part of recursion stack\n
    bool* visited = new bool[V];\n
    bool* recStack = new bool[V];\n
    for (int i = 0; i < V; i++) {\n
        visited[i] = false;\n
        recStack[i] = false;\n
    }\n

    // Call the recursive helper function\n
    // to detect cycle in different DFS trees\n
    for (int i = 0; i < V; i++)\n
        if (!visited[i] && isCyclicUtil(i, visited, recStack))\n
            return true;\n

    return false;\n
}\n

// Driver code\n
int main(){\n
    // Create a graph\n
    Graph g(4);\n
    g.addEdge(0, 1);\n
    g.addEdge(0, 2);\n
    g.addEdge(1, 2);\n
    g.addEdge(2, 0);\n
    g.addEdge(2, 3);\n
    g.addEdge(3, 3);\n

    // Function call\n
    if (g.isCyclic())\n
        cout << "Graph contains cycle";\n
    else\n
        cout << "Graph doesn't contain cycle";\n
    return 0;\n
}\n\n

C++ CODE:
 bool dfs(int st,bool visited[],vector<int>adj[],int par){
 visited[st]=true;
 bool isCycle=false;
 for(auto child:adj[st]){
 if(visited[child] && child==par) continue;
 if(visited[child]) return true;
 isCycle|=dfs(child,visited,adj,st);
 }
 return isCycle;
 }
 bool isCycle(int v, vector<int> adj[]) {
 bool visited[v+10]={0};
 for(int i=0;i<v;i++){
 if(visited[i]) continue;
 if(dfs(i,visited,adj,-1)) return true;
 }
 return false;
 }

C++ CODE:
 vector<int> minPartition(int N){
 //This is greedy approach work if sum of 2 coins is less than the 3rd one
 // 2+5 < 10
 //10 +20 < 50....
 //optimised approach is by DP
 vector<int> arr = {2000,500,200,100,50,20,10,5,2,1};
 vector<int>ans;
 int n = arr.size();
 int totalMoney = n;
 int i = 0;
 while(N){
 int no = N/arr[i];
 for(int j = 0; j < no; j++){
 ans.push_back(arr[i]);
 }
 N = N - no*arr[i];
 i++;
 }
 return ans;
 }

 JAVA CODE: class Solution {
 static List<Integer> minPartition(int N) {
 List<Integer> list = new ArrayList<Integer>();

int arr[] = {1, 2, 5, 10, 20, 50, 100, 200, 500, 2000};
 int ln =arr.length-1;

for(int i=ln; i>=0; i--){
 while(N >= arr[i] && N > 0){
 N = N - arr[i];
 list.add(arr[i]);
 }
 if(N == 0) break;
 }

return list;
 }
 }

