 class Solution{
 public:
 //Function to find the minimum number of platforms required at the railway station such that no train waits.
 int findPlatform(int arr[], int dep[], int n){
 map<int,int> mp;
 for(int i=0;i<n;i++){
 mp[arr[i]]++;
 mp[dep[i]+1]--;
 }
 int maxplat=1;
 int sum=0;
 for(auto t:mp){
 sum+=t.second;
 maxplat = max(maxplat,sum);
 }
 return maxplat;
 }
 };

 JAVA CODE: class Solution{
 static int findPlatform(int arr[], int dep[], int n){
 int max = 0, plat_occupied = 0;
 Arrays.sort(arr);
 Arrays.sort(dep);
 int i = 0, j = 0;
 while (i < n) {
 if (arr[i] <= dep[j]) {
 plat_occupied++;
 i++;
 } else if (arr[i] > dep[j]) {
 plat_occupied--;
 j++;
 }
 max = Math.max(max, plat_occupied);
 }
 return max;
 }
 }

