C++ CODE:
 struct node{
 int data;
 struct node* next;

node(int x){
 data = x;
 next = NULL;
 }
 }*head;
 struct node *reverse (struct node *head, int k){
 Node *p,*cr,*t;
 p=nullptr;
 cr=head;
 int d=k;
 while(cr&&d--){
 t=cr->next;
 cr->next=p;
 p=cr;
 cr=t;
 }
 if(cr) head->next=reverse(cr,k);
 return p;
 };

