 class Solution {
 public static Node reverse(Node node, int k) {
 if (k == 1) return node;
 else k -= 1;
 Node pre = new Node(0);
 pre.next = node;
 Node res = pre, p1 = node, p2 = p1.next;
 for (int cnt = 0; p2 != null; cnt++) {
 if (cnt != 0 && cnt % k == 0) {
 pre = p1;
 p1 = p1.next;
 p2 = p2.next;
 if (p2 == null) break;
 }
 p1.next = p2.next;
 p2.next = pre.next;
 pre.next = p2;
 p2 = p1.next;
 }
 return res.next;
 }
 }

C++ CODE:
 struct node{
 int data;
 struct node* next;

node(int x){
 data = x;
 next = NULL;
 }
 }*head;
 Node* reverse(Node* head){
 if(!head || !head->next)
 return head;

Node* curr = head;
 Node* prev = NULL;
 Node* Next = head->next;

while(curr->next!=NULL){
 curr->next = prev;
 prev = curr;
 curr = Next;
 Next = Next->next;
 }
 curr->next = prev;
 return curr;
 }

Node* addOne(Node *head){

Node* newHead = reverse(head);
 Node* temp = newHead;

while(temp->next != NULL && temp->data == 9){
 temp->data = 0;
 temp = temp->next;
 }
 if(temp->data != 9)
 temp->data += 1;
 else if(temp->next == NULL && temp->data == 9){
 temp->data = 0;
 temp->next = new Node(1);
 }

return reverse(newHead);
 }

