 class Solution {
 public static Node addOne(Node head) { 
 Node pre = new Node(0);
 pre.next = head;
 Node p1 = head, p2 = p1.next;

/* reverse the linked list */
 while (p2 != null) {
 p1.next = p2.next;
 p2.next = pre.next;
 pre.next = p2;
 p2 = p1.next;
 }
 p1 = pre.next;

/* add 1 to the linked list */
 p1.data += 1;
 while (p1.data == 10) {
 p1.data %= 10;
 if (p1.next == null) p1.next = new Node(0);
 p1.next.data += 1;
 p1 = p1.next;
 }

/* reverse the linked list again */
 p1 = pre.next;
 p2 = p1.next;
 while (p2 != null) {
 p1.next = p2.next;
 p2.next = pre.next;
 pre.next = p2;
 p2 = p1.next;
 }
 return pre.next;
 }
 }

C++ CODE:
 class Solution{
 public:
 vector<string> res;
 vector<int> dx;
 vector<int> dy;
 string dir;
 vector<string> findPath(vector<vector<int>> &m, int n) {
 dx={1,0,-1,0};
 dy = {0,1,0,-1};
 dir="DRUL";
 string temp;
 recur(m,0,0,temp);
 return res;
 }
 void recur(vector<vector<int>> &m,int r,int c,string &temp){
 // cout<<r<<c;
 // cout<<endl;
 if(r==m.size()-1 && c==m[0].size()-1 && m[r][c]!=0 ){
 res.push_back(temp);
 return ;
 }

if(r>=m.size() || r<0 || c>=m[0].size() || c<0 || m[r][c]==0 || m[r][c]==-1)
 return ;
 for(int i=0;i<4;i++){
 temp.push_back(dir[i]);
 int nx=r+dx[i];
 int ny=c+dy[i];
 int x=m[r][c];
 m[r][c]=-1;

recur(m,nx,ny,temp);
 m[r][c]=x;

temp.pop_back();
 }
 }
 };

 JAVA CODE: public static boolean isitSafe(int[][] m, int x, int y){
 if(x<0 || y<0 || x>=m.length || y>=m.length || m[x][y]==0) return false;
 return true;
 }

public static void ratinmaze(int[][] m, int x, int y, boolean visited[][], ArrayList<String> ans, List<Character> res){
 if(m[m.length-1][m.length-1]==0) return;
 if(x==m.length-1 && y==m.length-1){
 String s ="";
 for(Character ch: res)
 s += ""+ch;

ans.add(s);
 return;
 }

if(!isitSafe(m,x,y)) return;

visited[x][y] = true;
 if(isitSafe(m,x+1,y) && !visited[x+1][y]) {
 res.add("D");
 ratinmaze(m,x+1,y,visited,ans,res);
 res.remove(res.size()-1);
 }

if(isitSafe(m,x-1,y) && !visited[x-1][y]){
 res.add("U");
 ratinmaze(m,x-1,y,visited,ans,res);
 res.remove(res.size()-1);
 }

if(isitSafe(m,x,y+1) && !visited[x][y+1]){
 res.add("R");
 ratinmaze(m,x,y+1,visited,ans,res);
 res.remove(res.size()-1);
 }

if(isitSafe(m,x,y-1) && !visited[x][y-1]){
 res.add("L");
 ratinmaze(m,x,y-1,visited,ans,res);
 res.remove(res.size()-1);
 }

visited[x][y] = false;
 }

C++ CODE:
 set<string> ans;
 int c1;
 void help(string s, int i, string temp, int x, int c){
 if(i == s.size()){
 if(x == 0 && c == c1)
 ans.insert(temp);
 return;
 }
 if(s[i] == "(")
 help(s, i+1, temp+"(", x+1, c+1);
 else if(s[i] == ")" && x>0)
 help(s, i+1, temp+")", x-1, c+1);
 if(s[i] != "(" && s[i] != ")")
 help(s, i+1, temp+s[i], x, c);
 else
 help(s, i+1, temp, x, c);
 }
