 if(s[i] == ")" && x>0){
 x--;
 c+=2;
 }
 }
 c1 = c;
 help(s, 0, "", 0, 0);
 vector<string>res;
 for(auto i: ans)res.push_back(i);
 sort(res.begin(), res.end());
 return res;
 }

 JAVA CODE: static int findRemovals(String s){
 Stack<Character> st = new Stack<>();

for(int i = 0; i < s.length(); i++){
 if(s.charAt(i) == "("){
 st.push("(");
 }
 else if(s.charAt(i) == ")"){
 if(!st.isEmpty() && st.peek() == "("){
 st.pop();
 }
 else{
 st.push(")");
 }
 }
 }
 return st.size();
 }

static void solve(String s, Map<String, Boolean> mp, int rc, ArrayList<String> list){
 if(mp.containsKey(s)){
 return ;
 }
 mp.put(s, true);

if(rc == 0){
 if(findRemovals(s) == 0){
 list.add(s);
 return;
 } 
 }
 for(int i = 0; i < s.length(); i++){
 String left = s.substring(0, i);
 String right = s.substring(i+1);
 String join = left + right;
 solve(join, mp, rc-1, list);
 }
 return;
 }
 public static ArrayList<String> removeInvalidParentheses(String s) {
 int rc = findRemovals(s);
 Map<String, Boolean> mp = new HashMap<>();
 ArrayList<String> list = new ArrayList<>();
 solve(s, mp, rc, list);

return list;
 }

