 class Solution{
 public:
 //Function to check if brackets are balanced or not.
 bool ispar(string str){
 stack<char> stk;
 if (str[0] == ")" || str[0] == "}" || str[0] == "]")
 return false;
 if (str.length() % 2 != 0)
 return false;
 for (int i = 0;i<str.length();i++){
 if (str[i] == "(" || str[i] == "{" || str[i] == "[")
 stk.push(str[i]);
 else{
 if (!stk.empty()){
 if ((str[i] == ")" && stk.top() == "(") || (str[i] == "}" && stk.top() == "{") || (str[i] == "]" && stk.top() == "["))
 stk.pop();
 else
 return false;

 } else
 return false;
 }
 }
 if (stk.empty())
 return true;
 return false;
 }
 };

 JAVA CODE: class Solution{
 //Function to check if brackets are balanced or not.
 static boolean ispar(String x){
 int n=x.length();
 Stack<Character> st = new Stack<Character>();
 for(int i=0;i<n;i++){
 char ch=x.charAt(i);
 if(ch==")"){
 if(st.size()==0 || st.peek()!="(")return false;
 st.pop();
 }
 else if(ch=="}"){
 if(st.size()==0 || st.peek()!="{")return false;
 st.pop();
 }
 else if(ch=="]"){
 if(st.size()==0 || st.peek()!="[")return false;
 st.pop();
 }
 else{
 st.push(ch);
 }
 }
 return st.size()==0;
 }
 }

C++ CODE:
class Solution{\n
    public:\n
    //Function to find if there is a celebrity in the party or not.\n
    int celebrity(vector<vector<int> >& M, int n){\n
        //  Using stacks\n
        stack<int> st;\n
        for(int i = 0;i<n;i++){\n
            st.push(i);\n
        }\n

        while(st.size() > 1){\n
            int a = st.top();\n
            st.pop();\n
            int b = st.top();\n
            st.pop();\n

            if(M[a][b] == 1){\n
                st.push(b);\n
            }\n
            else{\n
                st.push(a);\n
            }\n
        }\n

        int celeb = st.top();\n
        for(int i = 0;i<n;i++){\n
            if(M[celeb][i] == 1){\n
                return -1;\n
            }\n
        }\n
        int knows = 1;\n
        for(int i = 0;i<n;i++){\n
            if(M[i][celeb] == 1){\n
                knows++;\n
            }\n
        }\n
        if(knows != n){\n
            return -1;\n
        }\n
        return celeb;\n
    }\n
};\n
JAVA CODE:
class Solution{\n
    //Function to find if there is a celebrity in the party or not.\n
    int celebrity(int M[][], int n){\n
        Stack<Integer> stack = new Stack<>();\n
        for(int i = 0; i< n;i++){\n
            stack.push(i);\n
        }\n

        while(stack.size() > 1){\n
            int a = stack.pop();\n
            int b = stack.pop();\n
            if(M[a][b] == 1){\n
                stack.push(b);\n
            }else{\n
                stack.push(a);\n
            }\n
        }\n
        int celeb = stack.peek();\n
        for(int i = 0; i < n ;i++){\n
            if(M[celeb][i] == 1){\n
                return -1;\n
            }\n
        }\n
        for(int i = 0; i < n ;i++){\n
            if(M[i][celeb] == 0 && i!=celeb){\n
                return -1;\n
            }\n
        }\n

        return celeb;\n
    }\n
}\n
