C++ CODE:

void sort012(int a[], int n){

    int num0=0,num2=0;

    for(int i=0;i < n;i++){

        if(a[i]==0) num0++;

        else if(a[i]==2) num2++;

    }

    for(int i=0;i < n  i++){

        if(i>=0 && i < num0) a[i]=0;

        else if(i>=num0 && i < (n-num2)) a[i]=1;

        else a[i]=2;

    }

}


JAVA CODE:

public static void sort012(int a[], int n){

    int zero=0, one=0, two=0;

    for(int i=0; i < a.length; i++){

        if(a[i] == 0){

            zero++;

        }

        else if(a[i] == 1){

            one++;

        }

        else{

            two++;

        }

    }

    for(int i=0; i < a.length; i++){

        if(i < zero){

            a[i] = 0;

        }

        else if(i>=zero && i < (one+zero)){

            a[i] = 1;

        }

        else{

            a[i] = 2;

        }

    }

}

C++ CODE:
 class Solution{
 public:
 void segregateElements(int arr[],int n){
 int j=0,k=0;
 int neg[n];
 for(int i=0;i < n;i++){
 if(arr[i] < 0) neg[k++] = arr[i];
 else arr[j++] = arr[i];
 }
 for(int i=0;j < n;j++,i++)
 arr[j] = neg[i];
 }
 }

 JAVA CODE:
 class Solution {
 public void segregateElements(int arr[], int n){
 if(n==0||n==1)
 return;
 int[] a=new int [n];
 int x=0;
 for(int i=0;i<n;i++){
 if(arr[i]>=0){
 a[x]=arr[i];
 x++;
 }
 }
 for(int i=0;i<n;i++){
 if(arr[i]<=0){
 a[x]=arr[i];
 x++;
 }
 }
 for(int i=0;i<n;i++){
 arr[i]=a[i];
 }
 }
 }

 PYTHON CODE:
 def segregateElements(self, arr, n):
 n=len(arr)
 x=[]
 for i in range(n):
 if arr[i]<0:
 arr=arr+[arr[i]]
 x=x+[arr[i]]
 for i in range(len(x)):
 arr.remove(x[i])
 return arr

C++ CODE:
 long long maxSub_arraySum(int arr[], int n){
 int res = arr[0];
 int curr = arr[0];
 for(int i=1;i < n;i++){
 curr = max(curr+arr[i], arr[i]);
 res = max(res,curr);
 }
 return res;
 }

 JAVA CODE:
 class Solution{
 long maxSub_arraySum(int arr[], int n){
 long max=Integer.MIN_VALUE;
 long sum=0;
 for(int i=0;i < n;i++){
 sum+=arr[i];
 if(max < sum)
 max=sum;
 //dont carry negative sum
 if(sum < 0)
 sum=0;
 }
 return max;
 }
 }

C++ CODE:
 class Solution{
 public:
 int next (int prev, int n){
 if(prev+1 > n/2) return (n-prev-1)*2;
 else{
 return (prev+1)*2-1;
 }
 }
 void arrange (int start, int n, long long *arr){
 int swap_index = next(start, n);
 while(arr[swap_index] > 0){
 swap(arr[swap_index], arr[start]);
 arr[swap_index] *= -1;
 swap_index = next(swap_index, n);
 }
 }
 void rearrange(long long *arr, int n){
 for(int i=0; i < n; i++){
 if(arr[i] > 0)
 arrange(i, n, arr);
 }
 for(int i=0; i < n; i++){
 arr[i] *= -1;
 }
 }
 }

 JAVA CODE:
 public static void rearrange(long arr[], int n){
 int max = (int)arr[n-1] + 1;
 int max_i = n-1;
 int min_i = 0;
 for(int i=0; i < n; i++){
 if(i%2 == 0){
 arr[i] = (arr[max_i]%max)*max + arr[i];
 max_i--;
 }
 else{
 arr[i] = (arr[min_i]%max)*max + arr[i];
 min_i++;
 }
 }

for(int i=0; i < n; i++){
 arr[i] = arr[i]/max;
 }
 }

C++ CODE:
 bool isPowerofTwo(long long n){
 if(n==0)return false;
 if((n & n-1)==0)return true;
 return false;
 }

 JAVA CODE:
 class Solution{
 // Function to check if given number n is a power of two.
 public static boolean isPowerofTwo(long n){
 if(n==1){
 return true;
 }
 if(n==0){
 return false;
 }
 while(n>1){
 if(n%2!=0){
 return false;
 }
 else{
 n=n/2;
 }
 }
 return true;
 }
 }

 PYTHON CODE:
 def isPowerofTwo(self,n):
 if n==0:
 return 0
